apples:
.input {$value :number}
.match $value
one   {{{#bold}How many apples:{/bold} {$value} apple}}
*     {{{#bold}How many apples:{/bold} {$value} apples}}

notifications:
.input {$name :string}
.input {$count :number}
.match $count
0   {{ {#bold}Hello {$name}!{/bold} You have no new messages }}
one {{ {#bold}Hello {$name}!{/bold} You have {$count} new message }}
*   {{ {#bold}Hello {$name}!{/bold} You have {$count} new messages }}
